package algorithms.medium;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;

public class PalindromicSubstringTest {
//    Example 1:
//
//    Input: s = "babad"
//    Output: "bab"
//    Explanation: "aba" is also a valid answer.
//            Example 2:
//
//    Input: s = "cbbd"
//    Output: "bb"
    @Test
    public void test1(){
        PalindromicSubstring palindromicSubstring = new PalindromicSubstring();
        String s = palindromicSubstring.longestPalindrome("babad");
        assertEquals("aba", s);
    }

    @Test
    public void test2(){
        PalindromicSubstring palindromicSubstring = new PalindromicSubstring();
        String s = palindromicSubstring.longestPalindrome("cbbd");
        assertEquals("bb", s);
    }


    @Test
    public void test3(){
        PalindromicSubstring palindromicSubstring = new PalindromicSubstring();
        String s = palindromicSubstring.longestPalindrome("a");
        assertEquals("a", s);
    }

    @Test
    public void test4(){
        PalindromicSubstring palindromicSubstring = new PalindromicSubstring();
        String s = palindromicSubstring.longestPalindrome("bb");
        assertEquals("bb", s);
    }

    @Test
    public void test5(){
        PalindromicSubstring palindromicSubstring = new PalindromicSubstring();
        String s = palindromicSubstring.longestPalindrome("dfbhrth");
        assertEquals("d", s);
    }    @Test
    public void test6(){
        PalindromicSubstring palindromicSubstring = new PalindromicSubstring();
        String s = palindromicSubstring.longestPalindrome("");
        assertEquals("", s);
    }
}